// Fetch unposted articles from database
const { Client } = require('pg');

const client = new Client({
  connectionString: $env.DATABASE_URL,
  ssl: { rejectUnauthorized: false }
});

await client.connect();

const query = `
  SELECT 
    id, 
    title, 
    excerpt, 
    "facebookHeadline", 
    "imageUrl", 
    "imageUrls", 
    category, 
    slug, 
    "interestScore"
  FROM articles
  WHERE "isPublished" = true
    AND "facebookPostId" IS NULL
    AND "interestScore" >= 4
    AND "imageUrl" IS NOT NULL
    AND "isManuallyCreated" = false
  ORDER BY "publishedAt" DESC
  LIMIT 5
`;

const result = await client.query(query);
await client.end();

console.log(`Found ${result.rows.length} articles to post`);

return result.rows;

const item = $input.item.json;

// Category-specific hashtags
const hashtagMap = {
  'Breaking': ['#PhuketNews', '#ThailandNews', '#BreakingNews'],
  'Tourism': ['#PhuketTourism', '#ThailandTravel', '#VisitPhuket'],
  'Business': ['#PhuketBusiness', '#ThailandBusiness', '#PhuketEconomy'],
  'Events': ['#PhuketEvents', '#ThingsToDoInPhuket', '#PhuketLife'],
  'Crime': ['#PhuketNews', '#PhuketCrime', '#ThailandSafety'],
  'Traffic': ['#PhuketTraffic', '#PhuketNews', '#ThailandTravel'],
  'Weather': ['#PhuketWeather', '#ThailandWeather', '#TropicalWeather']
};

const tags = hashtagMap[item.category] || ['#PhuketNews', '#Thailand', '#PhuketLife'];
const hashtags = `#Phuket ${tags.join(' ')}`;

// Build post message
const headline = item.facebookHeadline || item.title;
const postMessage = `${headline}\n\n${item.excerpt}\n\nWant the full story? Click the link in the first comment below...\n\n${hashtags}`;

// Build article URL
const categorySlug = item.category.toLowerCase();
const articleUrl = `${$env.SITE_BASE_URL}/${categorySlug}/${item.slug}-${item.id}`;

// Image handling
const imageUrls = item.imageUrls && item.imageUrls.length > 0 
  ? item.imageUrls 
  : (item.imageUrl ? [item.imageUrl] : []);

return {
  articleId: item.id,
  postMessage: postMessage,
  articleUrl: articleUrl,
  imageUrl: item.imageUrl,
  imageUrls: imageUrls,
  isMultiImage: imageUrls.length > 1
};

const item = $input.item.json;
const pageId = $env.FB_PAGE_ID;
const accessToken = $env.FB_PAGE_ACCESS_TOKEN;

console.log(`Posting article ${item.articleId} to Facebook...`);

let postId;

try {
  if (item.isMultiImage && item.imageUrls.length > 1) {
    // Multi-image post
    console.log(`Creating multi-image post with ${item.imageUrls.length} images`);

    const photoIds = [];

    // Upload each photo unpublished
    for (const imageUrl of item.imageUrls) {
      const uploadUrl = `https://graph.facebook.com/v18.0/${pageId}/photos`;
      const uploadParams = new URLSearchParams({
        access_token: accessToken,
        url: imageUrl,
        published: 'false'
      });

      const uploadResponse = await fetch(`${uploadUrl}?${uploadParams.toString()}`, {
        method: 'POST'
      });

      if (uploadResponse.ok) {
        const uploadData = await uploadResponse.json();
        photoIds.push(uploadData.id);
        console.log(`Uploaded photo: ${uploadData.id}`);
      } else {
        const error = await uploadResponse.text();
        console.error(`Failed to upload photo: ${error}`);
      }
    }

    // Create feed post with attached media
    if (photoIds.length > 1) {
      const feedUrl = `https://graph.facebook.com/v18.0/${pageId}/feed`;
      const feedParams = new URLSearchParams({
        access_token: accessToken,
        message: item.postMessage
      });

      // Add attached media
      photoIds.forEach((id, idx) => {
        feedParams.append(`attached_media[${idx}]`, JSON.stringify({ media_fbid: id }));
      });

      const feedResponse = await fetch(feedUrl, {
        method: 'POST',
        body: feedParams
      });

      if (feedResponse.ok) {
        const feedData = await feedResponse.json();
        postId = feedData.id;
        console.log(`Created multi-image post: ${postId}`);
      } else {
        const error = await feedResponse.text();
        console.error(`Failed to create feed post: ${error}`);
        throw new Error(`Facebook API error: ${error}`);
      }
    } else {
      // Fallback to single image
      console.log('Not enough photos uploaded, falling back to single image');
      item.isMultiImage = false;
    }
  }

  // Single image post (or fallback)
  if (!item.isMultiImage) {
    console.log('Creating single-image post');

    const photoUrl = `https://graph.facebook.com/v18.0/${pageId}/photos`;
    const photoParams = new URLSearchParams({
      access_token: accessToken,
      url: item.imageUrl,
      message: item.postMessage
    });

    const photoResponse = await fetch(photoUrl, {
      method: 'POST',
      body: photoParams
    });

    if (photoResponse.ok) {
      const photoData = await photoResponse.json();
      postId = photoData.post_id || photoData.id;
      console.log(`Created single-image post: ${postId}`);
    } else {
      const error = await photoResponse.text();
      console.error(`Failed to create photo post: ${error}`);
      throw new Error(`Facebook API error: ${error}`);
    }
  }

  if (!postId) {
    throw new Error('No post ID returned from Facebook');
  }

  return {
    ...item,
    postId: postId,
    postUrl: `https://www.facebook.com/${postId.replace('_', '/posts/')}`
  };

} catch (error) {
  console.error('Error posting to Facebook:', error);
  throw error;
}

const item = $input.item.json;
const accessToken = $env.FB_PAGE_ACCESS_TOKEN;

console.log('Adding comment with article link...');

const commentUrl = `https://graph.facebook.com/v18.0/${item.postId}/comments`;
const commentParams = new URLSearchParams({
  access_token: accessToken,
  message: `Read the full story: ${item.articleUrl}`
});

const commentResponse = await fetch(commentUrl, {
  method: 'POST',
  body: commentParams
});

if (commentResponse.ok) {
  const commentData = await commentResponse.json();
  console.log(`Comment added: ${commentData.id}`);

  return {
    ...item,
    commentId: commentData.id
  };
} else {
  const error = await commentResponse.text();
  console.error(`Failed to add comment: ${error}`);

  // Don't fail the whole process if comment fails
  return {
    ...item,
    commentId: null,
    commentError: error
  };
}

const item = $input.item.json;

if (!item.commentId) {
  console.log('No comment ID, skipping pin');
  return item;
}

const accessToken = $env.FB_PAGE_ACCESS_TOKEN;

console.log('Pinning comment...');

const pinUrl = `https://graph.facebook.com/v18.0/${item.commentId}`;
const pinParams = new URLSearchParams({
  access_token: accessToken,
  is_pinned: 'true'
});

const pinResponse = await fetch(`${pinUrl}?${pinParams.toString()}`, {
  method: 'POST'
});

if (pinResponse.ok) {
  const pinData = await pinResponse.json();
  console.log('Comment pinned successfully');
  return {
    ...item,
    commentPinned: true
  };
} else {
  const error = await pinResponse.text();
  console.log(`Warning: Failed to pin comment: ${error}`);
  return {
    ...item,
    commentPinned: false
  };
}

const item = $input.item.json;
const { Client } = require('pg');

const client = new Client({
  connectionString: $env.DATABASE_URL,
  ssl: { rejectUnauthorized: false }
});

await client.connect();

const query = `
  UPDATE articles
  SET "facebookPostId" = $1,
      "facebookPostUrl" = $2
  WHERE id = $3
`;

const values = [item.postId, item.postUrl, item.articleId];

await client.query(query, values);
await client.end();

console.log(`Updated article ${item.articleId} in database`);

return {
  articleId: item.articleId,
  postId: item.postId,
  postUrl: item.postUrl,
  success: true
};
